$(document).ready(function(){

  $(".custom-hamburger").click(function(){
    $(".hamburger-navbar").slideToggle("slow");
  })



  $(window).resize(function(){
    if ($(".custom-hamburger").css('display') == 'none') {
      $(".hamburger-navbar").css('display', 'none')
    }
  })

  //END OF JQUERY
})
